    for (var key in cameraSettings)
    {
        if (cameraSettings[key].enabled)
            commandString = commandString + ' -'+ key + ' ' + cameraSettings[key].value;
    }
    for (var key in timelapseSettings)
    {
        if (timelapseSettings[key].enabled && key != 'd')
        {
            commandString = commandString + ' -'+ key + ' ' + timelapseSettings[key].value;
        }
    }
    commandString = commandString + ' -o /media/pi/yggCamData/images/latest.jpg';
    flow.set('commandLineArgs',commandString);
    var maxImages = Math.round((timelapseSettings['d'].value) / (Number(timelapseSettings['t'].value) / 1000));
    flow.set('maxImages', maxImages);
    return [null,msg];    
}

